mapper.select:
select * from inquiry
where mno = #{mno}
order by reg_at desc

select * from inquiry


        and status = #{status}


order by reg_at desc

select m.login_id from member m
join inquiry i ON m.mno = i.mno
where i.ino = #{ino}

mapper.update:
update inquiry
set response = #{response}, status = '답변 완료', mod_at = now()
where ino = #{ino}
